import React, { useState } from "react";
import axios from "axios";

const VideoUpload = ({ onUploadSuccess }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [uploadStatus, setUploadStatus] = useState("");

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setPreviewURL(URL.createObjectURL(file));
    setUploadStatus("");
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append("video", selectedFile);

    try {
      const res = await axios.post("/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setUploadStatus("Upload successful!");
      onUploadSuccess(res.data.filename);
    } catch (err) {
      setUploadStatus("Upload failed.");
      console.error("Upload error:", err);
    }
  };

  return (
    <div style={{
      border: "1px solid #ddd",
      borderRadius: "8px",
      padding: "1.5rem",
      marginBottom: "1.5rem",
      maxWidth: "600px",
      backgroundColor: "#fafafa"
    }}>
      <h3 style={{ marginBottom: "1rem" }}>Select a video to review</h3>

      <label htmlFor="video-file" style={{
        display: "block",
        marginBottom: "0.5rem",
        fontWeight: "500"
      }}>
        Choose a video file:
      </label>

      <input
        id="video-file"
        type="file"
        accept="video/*"
        onChange={handleFileChange}
        style={{
          marginBottom: "1rem",
          padding: "0.4rem",
          border: "1px solid #ccc",
          borderRadius: "4px",
          width: "100%"
        }}
      />

      {selectedFile && (
        <div style={{ marginBottom: "1rem", fontStyle: "italic" }}>
          Selected: {selectedFile.name}
        </div>
      )}

      <button
        onClick={handleUpload}
        disabled={!selectedFile}
        style={{
          backgroundColor: selectedFile ? "#1976d2" : "#aaa",
          color: "#fff",
          border: "none",
          padding: "0.6rem 1.5rem",
          borderRadius: "4px",
          fontSize: "1rem",
          cursor: selectedFile ? "pointer" : "not-allowed",
          display: "block",
          margin: "0 auto"
        }}
      >
        Upload Video
      </button>

      {uploadStatus && <p style={{ marginTop: "1rem", fontWeight: 600 }}>{uploadStatus}</p>}
    </div>
  );
};

export default VideoUpload;
