import React, { useEffect, useState } from "react";
import axios from "axios";

const CommentList = () => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/comments/demo-video").then((res) => {
      setComments(res.data);
    });
  }, []);

  return (
    <div>
      <h4>Comments</h4>
      <ul>
        {comments.map((c, index) => (
          <li key={index}>
            <strong>{c.timestamp}:</strong> {c.comment} ({c.user})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommentList;
