import React, { useState, useEffect } from "react";
import VideoPlayer from "./components/VideoPlayer";
import CommentForm from "./components/CommentForm";
import CommentList from "./components/CommentList";
import AuthForm from "./components/AuthForm";
import VideoUpload from "./components/VideoUpload";

function App() {
  const [user, setUser] = useState(() => {
    const token = localStorage.getItem("token");
    const username = localStorage.getItem("username");
    return token ? { token, username } : null;
  });

  const [videoId, setVideoId] = useState(() => localStorage.getItem("videoId") || null);
  const [videoFilename, setVideoFilename] = useState(() => localStorage.getItem("videoFilename") || null);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    setUser(null);
  };

  if (!user) {
    return <AuthForm onAuthSuccess={setUser} />;
  }

  return (
    <div style={{ padding: "2rem" }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h2>Naveon Video Review</h2>
        <button onClick={handleLogout} style={{ padding: "0.5rem 1rem", background: "#d32f2f", color: "white", border: "none", borderRadius: "4px", cursor: "pointer" }}>
          Logout ({user.username})
        </button>
      </div>

      {!videoFilename ? (
        <VideoUpload onUploadSuccess={(filename) => {
          const baseName = filename.replace(/\.[^/.]+$/, "");
          setVideoId(baseName);
          setVideoFilename(filename);
          localStorage.setItem("videoId", baseName);
          localStorage.setItem("videoFilename", filename);
        }} />
      ) : (
        <div style={{ margin: "1rem 0", padding: "1rem", border: "1px solid #ccc", borderRadius: "6px", background: "#f9f9f9" }}>
          üéûÔ∏è Reviewing: <strong>{videoFilename}</strong>{" "}
          <button onClick={() => {
            setVideoFilename(null);
            setVideoId(null);
            localStorage.removeItem("videoFilename");
            localStorage.removeItem("videoId");
          }} style={{ marginLeft: "1rem", padding: "0.25rem 0.75rem", fontSize: "0.9rem", cursor: "pointer" }}>
            Change
          </button>
        </div>
      )}

      <VideoPlayer filename={videoFilename} />
      <CommentForm videoId={videoId} />
      <CommentList videoId={videoId} />
    </div>
  );
}

export default App;
