import React, { useState } from "react";
import axios from "axios";

const VideoUpload = ({ onUploadSuccess }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const [uploadStatus, setUploadStatus] = useState("");

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setPreviewURL(URL.createObjectURL(file));
    setUploadStatus("");
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append("video", selectedFile);

    try {
      const res = await axios.post("/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setUploadStatus("Upload successful!");
      onUploadSuccess(res.data.filename);
    } catch (err) {
      setUploadStatus("Upload failed.");
      console.error("Upload error:", err);
    }
  };

  return (
    <div style={{ marginBottom: "1rem", padding: "1rem", border: "1px solid #ccc", borderRadius: "8px" }}>
      <div style={{ display: "flex", alignItems: "center", gap: "1rem" }}>
        <label style={{ fontWeight: 500 }}>Select video:</label>
        <input
          type="file"
          accept="video/*"
          onChange={handleFileChange}
          style={{
            padding: "0.3rem",
            border: "1px solid #ccc",
            borderRadius: "4px"
          }}
        />
        <button
          onClick={handleUpload}
          disabled={!selectedFile}
          style={{
            backgroundColor: "#1976d2",
            color: "#fff",
            border: "none",
            padding: "0.5rem 1rem",
            borderRadius: "4px",
            cursor: selectedFile ? "pointer" : "not-allowed"
          }}
        >
          Upload Video
        </button>
      </div>

      {selectedFile && !uploadStatus && (
        <div style={{ marginTop: "0.5rem", fontStyle: "italic" }}>
          Selected: {selectedFile.name}
        </div>
      )}

      {uploadStatus && <p style={{ marginTop: "0.5rem", fontWeight: 600 }}>{uploadStatus}</p>}

      {previewURL && !uploadStatus && (
        <div style={{ marginTop: "1rem" }}>
          <video src={previewURL} controls width="480" />
        </div>
      )}
    </div>
  );
};

export default VideoUpload;
