import React, { useEffect, useState } from "react";
import axios from "axios";

const CommentList = () => {
  const [comments, setComments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedTextMap, setEditedTextMap] = useState({});

  useEffect(() => {
    axios.get("/comments/demo-video", {
      withCredentials: false
    })
    .then((res) => {
      setComments(res.data);
    })
    .catch((err) => {
      console.error("Failed to load comments:", err);
    });
  }, []);

  const startEditing = (comment) => {
    if (!comment || typeof comment.id !== "number") {
      console.warn("Invalid comment selected for editing:", comment);
      return;
    }
    console.log("Editing comment ID:", comment.id);
    setEditingId(comment.id);
    setEditedTextMap((prev) => ({ ...prev, [comment.id]: comment.comment }));
  };

  const cancelEditing = () => {
    setEditingId(null);
    setEditedTextMap({});
  };

  const handleTextChange = (id, value) => {
    setEditedTextMap((prev) => ({ ...prev, [id]: value }));
  };

  const saveEdit = async (id) => {
    const updatedText = editedTextMap[id];
    if (typeof id !== "number" || typeof updatedText !== "string") {
      console.warn("Invalid save request:", { id, updatedText });
      return;
    }

    try {
      console.log(`Saving comment ID ${id}:`, updatedText);
      await axios.put(`/comments/${id}`, { comment: updatedText });
      const res = await axios.get("/comments/demo-video", {
        withCredentials: false
      });
      console.log("Fetched updated comments:", res.data);
      setComments(res.data);
      setEditingId(null);
      setEditedTextMap({});
    } catch (err) {
      console.error("Failed to update comment:", err);
    }
  };

  return (
    <div>
      <h4>Comments</h4>
      <ul>
        {comments.map((c, i) => {
          const commentId = typeof c.id === "number" ? c.id : i;
          return (
            <li key={commentId}>
              <strong>{c.timestamp}:</strong>{" "}
              {editingId === commentId ? (
                <div>
                  <textarea
                    value={editedTextMap[commentId] || ""}
                    onChange={(e) => handleTextChange(commentId, e.target.value)}
                    rows={2}
                    cols={50}
                  />
                  <br />
                  <button onClick={() => saveEdit(commentId)}>Save</button>
                  <button onClick={cancelEditing}>Cancel</button>
                </div>
              ) : (
                <div>
                  {c.comment} ({c.user}){" "}
                  <button onClick={() => startEditing({ ...c, id: commentId })}>Edit</button>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default CommentList;
