import React, { useEffect, useState } from "react";
import axios from "axios";

const CommentList = () => {
  const [comments, setComments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedTextMap, setEditedTextMap] = useState({});

  const [reactionCounts, setReactionCounts] = useState({});

  const addReaction = (id, type) => {
    setReactionCounts((prev) => ({
      ...prev,
      [id]: {
        ...prev[id],
        [type]: (prev[id]?.[type] || 0) + 1
      }
    }));
  };

  useEffect(() => {
    const fetchComments = () => {
      axios.get("/comments/demo-video", {
        withCredentials: false
      })
      .then((res) => {
        setComments(res.data);
      })
      .catch((err) => {
        console.error("Failed to load comments:", err);
      });
    };

    fetchComments();
    const listener = () => fetchComments();
    window.addEventListener("comments-updated", listener);

    return () => {
      window.removeEventListener("comments-updated", listener);
    };
  }, []);

  const startEditing = (comment) => {
    const id = comment.id ?? `${comment.timestamp}-${comment.comment}`;
    if (!id) {
      console.warn("Invalid comment selected for editing:", comment);
      return;
    }
    console.log("Editing comment ID:", id);
    setEditingId(id);
    setEditedTextMap((prev) => ({ ...prev, [id]: comment.comment }));
  };

  const cancelEditing = () => {
    setEditingId(null);
    setEditedTextMap({});
  };

  const handleTextChange = (id, value) => {
    setEditedTextMap((prev) => ({ ...prev, [id]: value }));
  };

  const saveEdit = async (id) => {
    const updatedText = editedTextMap[id];
    if (typeof id !== "number") {
      console.warn("Skipping save for fallback ID:", id);
      return;
    }

    try {
      console.log(`Saving comment ID ${id}:`, updatedText);
      await axios.put(`/comments/${id}`, { comment: updatedText });
      const res = await axios.get("/comments/demo-video", {
        withCredentials: false
      });
      console.log("Fetched updated comments:", res.data);
      setComments(res.data);
      setEditingId(null);
      setEditedTextMap({});
    } catch (err) {
      console.error("Failed to update comment:", err);
    }
  };

  const deleteComment = async (id) => {
    if (!window.confirm("Are you sure you want to delete this comment?")) return;
    try {
      await axios.delete(`/comments/${id}`);
      const res = await axios.get("/comments/demo-video", {
        withCredentials: false
      });
      setComments(res.data);
    } catch (err) {
      console.error("Failed to delete comment:", err);
    }
  };

  // Button styles for consistent look
  const buttonStyle = {
    backgroundColor: "#1976d2",
    color: "white",
    border: "none",
    borderRadius: "4px",
    padding: "0.35rem 0.75rem",
    fontSize: "0.9rem",
    cursor: "pointer"
  };
  const cancelButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#aaa"
  };
  const deleteButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#d32f2f"
  };

  return (
    <div style={{ background: "#f9f9f9", padding: "1rem", borderRadius: "8px", boxShadow: "0 1px 4px rgba(0,0,0,0.1)" }}>
      <h4 style={{ marginBottom: "1rem" }}>Comments</h4>
      <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
        {comments.map((c) => {
          if (!c || !c.timestamp || !c.comment) return null;
          const commentId = c.id ?? `${c.timestamp}-${c.comment}`;
          return (
            <li
              key={commentId}
              style={{
                background: "#fff",
                border: "1px solid #ddd",
                borderRadius: "6px",
                padding: "0.75rem",
                marginBottom: "0.75rem"
              }}
            >
              <strong style={{ display: "block", color: "#555", marginBottom: "0.25rem" }}>
                {c.timestamp}
              </strong>
              {editingId === commentId ? (
                <div>
                  <textarea
                    value={editedTextMap[commentId] || ""}
                    onChange={(e) => handleTextChange(commentId, e.target.value)}
                    rows={2}
                    cols={50}
                    style={{
                      width: "100%",
                      borderRadius: "4px",
                      border: "1px solid #ccc",
                      padding: "0.5rem",
                      fontSize: "0.95rem",
                      marginBottom: "0.5rem"
                    }}
                  />
                  <div style={{ display: "flex", gap: "0.5rem" }}>
                    <button style={buttonStyle} onClick={() => saveEdit(commentId)}>Save</button>
                    <button style={cancelButtonStyle} onClick={cancelEditing}>Cancel</button>
                  </div>
                </div>
              ) : (
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                  <span style={{ color: "#333" }}>
                    {c.comment} <span style={{ color: "#888" }}>({c.user})</span>
                  </span>
                  <div style={{ display: "flex", gap: "0.5rem", alignItems: "center" }}>
                    <button style={buttonStyle} onClick={() => startEditing(c)}>Edit</button>
                    <button style={deleteButtonStyle} onClick={() => deleteComment(c.id)}>Delete</button>
                    <div style={{ display: "flex", gap: "0.25rem", fontSize: "1.2rem" }}>
                      {["ðŸ‘", "â¤ï¸", "ðŸ‘Ž"].map((icon) => (
                        <button
                          key={icon}
                          onClick={() => addReaction(c.id, icon)}
                          style={{
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            fontSize: "1.2rem"
                          }}
                        >
                          {icon} {reactionCounts[c.id]?.[icon] || ""}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default CommentList;
