import React, { useEffect, useRef, useState } from "react";

const VideoPlayer = ({ videoUrl, comments = [] }) => {
  const [activeComment, setActiveComment] = useState(null);
  const [filterType, setFilterType] = useState("all");
  const [hoveredComment, setHoveredComment] = useState(null);
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleTimeUpdate = () => {
      const current = Math.floor(video.currentTime);
      let relevantComments = comments;
      if (filterType === "silas") {
        relevantComments = comments.filter(c => c.user && c.user.toLowerCase().includes("silas"));
      } else if (filterType === "human") {
        relevantComments = comments.filter(c => !c.user || !c.user.toLowerCase().includes("silas"));
      }
      const match = relevantComments.find(c => Math.abs(Number(c.timestamp) - current) <= 1);
      setActiveComment(match || null);
    };

    video.addEventListener("timeupdate", handleTimeUpdate);
    return () => video.removeEventListener("timeupdate", handleTimeUpdate);
  }, [comments, filterType]);

  if (!videoUrl) return null;
  console.log("ðŸ“º Loading video from:", videoUrl);

  let viewer;

  if (videoUrl.endsWith(".mp4")) {
    viewer = (
      <>
        <div style={{ textAlign: "right", marginBottom: "8px", maxWidth: "960px", margin: "0 auto" }}>
          <label style={{ fontSize: "0.85rem", marginRight: "0.5rem" }}>Filter:</label>
          <select
            value={filterType}
            onChange={e => setFilterType(e.target.value)}
            style={{ fontSize: "0.85rem", padding: "0.2rem" }}
          >
            <option value="all">All Comments</option>
            <option value="silas">SILAS Only</option>
            <option value="human">Human Reviewers Only</option>
          </select>
        </div>
        <div style={{ position: "relative", width: "100%", maxWidth: "960px", margin: "0 auto" }}>
          <video
            id="review-video"
            ref={videoRef}
            key={videoUrl}
            controls
            style={{ width: "100%", height: "auto", display: "block" }}
          >
            <source src={videoUrl} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
          <div style={{ position: "absolute", bottom: "38px", width: "100%", height: "10px", pointerEvents: "none" }}>
            {comments.map((c, index) => {
              const percent = videoRef.current && videoRef.current.duration ? (Number(c.timestamp) / videoRef.current.duration) * 100 : 0;
              return (
                <div
                  key={index}
                  onClick={() => {
                    if (videoRef.current) videoRef.current.currentTime = Number(c.timestamp);
                  }}
                  onMouseEnter={() => setHoveredComment(c)}
                  onMouseLeave={() => setHoveredComment(null)}
                  style={{
                    position: "absolute",
                    left: `${percent}%`,
                    width: "8px",
                    height: "8px",
                    background: "#3b82f6",
                    borderRadius: "50%",
                    transform: "translateX(-50%)",
                    pointerEvents: "auto",
                    cursor: "pointer"
                  }}
                />
              );
            })}
          </div>
          {hoveredComment && (
            <div style={{
              position: "absolute",
              bottom: "50px",
              left: "10px",
              background: "#ffffff",
              color: "#000",
              padding: "6px 10px",
              borderRadius: "4px",
              boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
              fontSize: "0.8rem",
              maxWidth: "280px",
              zIndex: 20
            }}>
              <div style={{ fontWeight: "bold", marginBottom: "4px" }}>
                {hoveredComment.user || "Reviewer"} @ {hoveredComment.timestamp}s
              </div>
              <div>{hoveredComment.comment}</div>
            </div>
          )}
          {activeComment && (
            <div style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              background: "#fff",
              padding: "8px 12px",
              borderRadius: "6px",
              boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
              fontSize: "0.9rem",
              zIndex: 10
            }}>
              <div style={{ fontSize: "0.85rem", fontWeight: "bold" }}>
                {activeComment.user || "Reviewer"} @ {activeComment.timestamp}s
              </div>
              <div>{activeComment.comment}</div>
            </div>
          )}
        </div>
      </>
    );
  } else if (videoUrl.endsWith(".mp3") || videoUrl.endsWith(".wav")) {
    viewer = (
      <audio
        key={videoUrl}
        controls
        style={{ width: "100%", maxWidth: "960px", display: "block", margin: "0 auto" }}
      >
        <source src={videoUrl} />
        Your browser does not support the audio tag.
      </audio>
    );
  } else if (videoUrl.endsWith(".pdf")) {
    viewer = (
      <iframe
        src={videoUrl}
        title="PDF viewer"
        width="100%"
        height="600px"
        style={{ display: "block", margin: "0 auto", border: "1px solid #ccc" }}
      />
    );
  } else {
    viewer = <p style={{ textAlign: "center", marginTop: "2rem" }}>Unsupported file type.</p>;
  }

  return <div>{viewer}</div>;
};

export default VideoPlayer;
