import React, { useEffect, useState } from "react";
import axios from "axios";

const CommentList = () => {
  const [comments, setComments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editedTextMap, setEditedTextMap] = useState({});

  useEffect(() => {
    axios.get("/comments/demo-video", {
      withCredentials: false
    })
    .then((res) => {
      setComments(res.data);
    })
    .catch((err) => {
      console.error("Failed to load comments:", err);
    });
  }, []);

  const startEditing = (comment) => {
    setEditingId(comment.id);
    setEditedTextMap({ [comment.id]: comment.comment });
  };

  const cancelEditing = () => {
    setEditingId(null);
    setEditedTextMap({});
  };

  const handleTextChange = (id, value) => {
    setEditedTextMap((prev) => ({ ...prev, [id]: value }));
  };

  const saveEdit = async () => {
    if (!editingId || !editedTextMap[editingId]) return;
    try {
      const updatedText = editedTextMap[editingId];
      await axios.put(`/comments/${editingId}`, {
        comment: updatedText
      });
      const updated = comments.map((comment) =>
        comment.id === editingId ? { ...comment, comment: updatedText } : comment
      );
      setComments(updated);
      cancelEditing();
    } catch (err) {
      console.error("Failed to update comment:", err);
    }
  };

  return (
    <div>
      <h4>Comments</h4>
      <ul>
        {comments.map((c) => (
          <React.Fragment key={c.id}>
            <li>
              <div>
                <strong>{c.timestamp}:</strong>{" "}
                {editingId === c.id ? (
                  <div>
                    <textarea
                      value={editedTextMap[c.id] || ""}
                      onChange={(e) => handleTextChange(c.id, e.target.value)}
                      rows={2}
                      cols={50}
                    />
                    <br />
                    <button onClick={saveEdit}>Save</button>
                    <button onClick={cancelEditing}>Cancel</button>
                  </div>
                ) : (
                  <div>
                    {c.comment} ({c.user}){" "}
                    <button onClick={() => startEditing(c)}>Edit</button>
                  </div>
                )}
              </div>
            </li>
          </React.Fragment>
        ))}
      </ul>
    </div>
  );
};

export default CommentList;
