import React, { useRef, useEffect, useState } from "react";
import * as pdfjsLib from "pdfjs-dist";
import { GlobalWorkerOptions } from "pdfjs-dist/build/pdf";
GlobalWorkerOptions.workerSrc = "/pdf.worker.mjs";

const FADE_CLASS = "pdf-fade-in";

const PdfRegionCommenter = ({ url }) => {
  const containerRef = useRef(null);
  const [pdf, setPdf] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [scale, setScale] = useState(0.85);
  const [autoScale, setAutoScale] = useState(null);
  const [mode, setMode] = useState("highlight"); // "highlight" or "select"
  const [zoomMode, setZoomMode] = useState("default");
  const prevPageRef = useRef(currentPage);

  useEffect(() => {
    const loadPdf = async () => {
      const loadingTask = pdfjsLib.getDocument(url);
      const loadedPdf = await loadingTask.promise;
      setPdf(loadedPdf);
      const page = await loadedPdf.getPage(1);
      const view = page.getViewport({ scale: 1 });

      const container = containerRef.current?.parentElement; // the flex row wrapper
      const containerHeight = 650;
      const containerWidth = container?.offsetWidth || 800;

      const scaleY = containerHeight / view.height;
      const scaleX = (containerWidth - 250) / view.width; // subtract sidebar width
      const bestFitScale = Math.min(scaleX, scaleY);

      setAutoScale(bestFitScale);
      setScale(bestFitScale);
    };
    loadPdf();
  }, [url]);

  useEffect(() => {
    if (!pdf) return;

    const renderPage = async () => {
      const container = containerRef.current;
      container.innerHTML = ""; // clear previous

      const page = await pdf.getPage(currentPage);
      const viewport = page.getViewport({ scale });

      const canvas = document.createElement("canvas");
      canvas.width = viewport.width;
      canvas.height = viewport.height;
      canvas.style.border = "1px solid #ccc";
      const context = canvas.getContext("2d");

      const renderContext = {
        canvasContext: context,
        viewport: viewport
      };

      await page.render(renderContext).promise;

      const wrapper = document.createElement("div");
      wrapper.style.position = "relative";
      wrapper.style.width = `${viewport.width}px`;
      wrapper.style.height = `${viewport.height}px`;
      wrapper.style.marginBottom = "1rem";
      wrapper.style.boxShadow = "0 0 6px rgba(0,0,0,0.1)";
      wrapper.style.backgroundColor = "#fff";
      wrapper.style.borderRadius = "4px";
      wrapper.style.padding = "0.5rem";
      wrapper.style.display = "flex";
      wrapper.style.justifyContent = "center";
      wrapper.style.alignItems = "flex-start";
      wrapper.classList.add(FADE_CLASS);

      wrapper.appendChild(canvas);

      if (mode === "select") {
        const textContent = await page.getTextContent();
        const textLayerDiv = document.createElement("div");
        textLayerDiv.style.position = "absolute";
        textLayerDiv.style.top = 0;
        textLayerDiv.style.left = 0;
        textLayerDiv.style.width = "100%";
        textLayerDiv.style.height = "100%";
        textLayerDiv.className = "textLayer";

        wrapper.appendChild(textLayerDiv);

        textContent.items.forEach((item) => {
          const span = document.createElement("span");
          span.textContent = item.str;
          const [x, y, , , , fontHeight] = item.transform;
          span.style.position = "absolute";
          span.style.left = `${x}px`;
          span.style.top = `${y - fontHeight}px`;
          span.style.fontSize = `${fontHeight}px`;
          span.style.userSelect = "text";
          span.style.whiteSpace = "pre";
          span.style.pointerEvents = "auto";
          textLayerDiv.appendChild(span);
        });
      }

      const overlay = document.createElement("div");
      overlay.style.position = "absolute";
      overlay.style.top = 0;
      overlay.style.left = 0;
      overlay.style.width = "100%";
      overlay.style.height = "100%";
      overlay.style.cursor = mode === "highlight" ? "crosshair" : "text";
      if (mode === "select") {
        overlay.style.pointerEvents = "none";
        wrapper.appendChild(overlay);
        container.appendChild(wrapper);
        if (prevPageRef.current !== currentPage) {
          container.scrollTop = 0;
        }
        prevPageRef.current = currentPage;
        return;
      }
      wrapper.appendChild(overlay);

      (function(pageNum) {
        let isDragging = false;
        let startX = 0;
        let startY = 0;
        let rect = null;

        overlay.addEventListener("mousedown", (e) => {
          isDragging = true;
          startX = e.offsetX;
          startY = e.offsetY;

          if (rect && overlay.contains(rect)) {
            overlay.removeChild(rect);
          }

          rect = document.createElement("div");
          rect.style.position = "absolute";
          rect.style.border = "2px solid red";
          rect.style.background = "rgba(255,0,0,0.2)";
          rect.style.left = `${startX}px`;
          rect.style.top = `${startY}px`;
          overlay.appendChild(rect);
        });

        overlay.addEventListener("mousemove", (e) => {
          if (!isDragging || !rect) return;
          const width = e.offsetX - startX;
          const height = e.offsetY - startY;
          rect.style.width = `${Math.abs(width)}px`;
          rect.style.height = `${Math.abs(height)}px`;
          rect.style.left = `${width < 0 ? e.offsetX : startX}px`;
          rect.style.top = `${height < 0 ? e.offsetY : startY}px`;
        });

        overlay.addEventListener("mouseup", (e) => {
          isDragging = false;
          if (rect) {
            const x = parseFloat(rect.style.left);
            const y = parseFloat(rect.style.top);
            const w = parseFloat(rect.style.width);
            const h = parseFloat(rect.style.height);
            console.log("üñºÔ∏è Region drawn:", { x, y, w, h, pageNum });
            rect.title = `Page ${pageNum}\nX: ${x}\nY: ${y}\nW: ${w}\nH: ${h}`;
          }
        });
      })(currentPage);

      container.appendChild(wrapper);
      if (prevPageRef.current !== currentPage) {
        container.scrollTop = 0;
      }
      prevPageRef.current = currentPage;
    };

    renderPage();
  }, [pdf, currentPage, scale, mode]);

  return (
    <>
      <style>{`
        .pdf-fade-in {
          animation: fadeIn 0.3s ease-in;
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        .textLayer {
          font-family: sans-serif;
          font-size: 1em;
          line-height: 1;
          color: black;
          opacity: 0.8;
          pointer-events: auto;
          user-select: text;
        }
      `}</style>
      <div style={{ display: "flex", gap: "1rem" }}>
        <div style={{ width: "200px", display: "flex", flexDirection: "column", gap: "1rem" }}>
          <h3>Tools</h3>
          <label>
            Zoom:
            <select value={zoomMode} onChange={async (e) => {
              const value = e.target.value;
              setZoomMode(value);
              if (value === "fit-width") {
                const container = containerRef.current?.parentElement;
                if (pdf && container) {
                  const page = await pdf.getPage(currentPage);
                  const view = page.getViewport({ scale: 1 });
                  const availableWidth = container?.offsetWidth || 800;
                  const scaleX = (availableWidth - 250) / view.width;
                  setScale(scaleX);
                }
              } else if (value === "default") {
                setScale(autoScale);
              } else {
                setScale(parseFloat(value));
              }
            }}>
              <option value="default">Default ({autoScale ? Math.round(autoScale * 100) : 0}%)</option>
              <option value="fit-width">Fit to Width</option>
              <option value="0.25">25%</option>
              <option value="0.5">50%</option>
              <option value="0.75">75%</option>
              <option value="1">100%</option>
              <option value="1.25">125%</option>
              <option value="1.5">150%</option>
            </select>
          </label>
          <label>
            Mode:
            <select value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="highlight">Highlight</option>
              <option value="select">Select Text</option>
            </select>
          </label>
          <div>
            <button onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}>Previous</button>
            <div style={{ margin: "0.5rem 0" }}>Page {currentPage} of {pdf?.numPages || "..."}</div>
            <button onClick={() => setCurrentPage((p) => Math.min(p + 1, pdf?.numPages || 1))}>Next</button>
          </div>
        </div>

        <div
          ref={containerRef}
          key={scale}
          style={{
            maxHeight: "650px",
            overflowY: "auto",
            border: "1px solid #ccc",
            padding: "1rem",
            backgroundColor: "#fff",
            display: "flex",
            justifyContent: "center",
            flexGrow: 1
          }}
        />
      </div>
    </>
  );
};

export default PdfRegionCommenter;
