import React, { useState, useEffect } from "react";
import axios from "axios";

const CommentForm = () => {
  const [comment, setComment] = useState("");
  const [pausedTime, setPausedTime] = useState("0.00");

  useEffect(() => {
    const video = document.getElementById("review-video");
    if (video) {
      const handlePause = () => {
        setPausedTime(video.currentTime.toFixed(2));
      };
      video.addEventListener("pause", handlePause);
      return () => video.removeEventListener("pause", handlePause);
    }
  }, []);

  const handleSubmit = async () => {
    const video = document.getElementById("review-video");
    const timestamp = video?.paused ? pausedTime : video?.currentTime.toFixed(2) || "0.00";

    await axios.post("/comments", {
      video_id: "demo-video",
      timestamp,
      comment,
      user: "Reviewer"
    }, {
      withCredentials: false
    })
    .then(() => setComment(""))
    .catch((err) => {
      console.error("Failed to submit comment:", err);
    });
  };

  return (
    <div>
      <textarea
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        placeholder="Enter your comment..."
        rows="4"
        cols="60"
      />
      <br />
      <button onClick={handleSubmit}>Add Comment</button>
    </div>
  );
};

export default CommentForm;
