import React, { useRef, useEffect, useState } from "react";
import * as pdfjsLib from "pdfjs-dist";
import { GlobalWorkerOptions, version } from "pdfjs-dist/build/pdf";
GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${version}/pdf.worker.min.js`;

const PdfRegionCommenter = ({ url }) => {
  const containerRef = useRef(null);
  const [pdf, setPdf] = useState(null);

  useEffect(() => {
    const loadPdf = async () => {
      const loadingTask = pdfjsLib.getDocument(url);
      const loadedPdf = await loadingTask.promise;
      setPdf(loadedPdf);
    };
    loadPdf();
  }, [url]);

  useEffect(() => {
    if (!pdf) return;

    const renderPages = async () => {
      const container = containerRef.current;
      container.innerHTML = ""; // clear previous

      for (let pageNum = 1; pageNum <= pdf.numPages; pageNum++) {
        const page = await pdf.getPage(pageNum);
        const viewport = page.getViewport({ scale: 1.5 });

        const canvas = document.createElement("canvas");
        canvas.width = viewport.width;
        canvas.height = viewport.height;
        canvas.style.border = "1px solid #ccc";
        const context = canvas.getContext("2d");

        const renderContext = {
          canvasContext: context,
          viewport: viewport
        };

        await page.render(renderContext).promise;

        const wrapper = document.createElement("div");
        wrapper.style.position = "relative";
        wrapper.style.width = `${viewport.width}px`;
        wrapper.style.height = `${viewport.height}px`;
        wrapper.style.marginBottom = "1rem";

        wrapper.appendChild(canvas);

        const overlay = document.createElement("div");
        overlay.style.position = "absolute";
        overlay.style.top = 0;
        overlay.style.left = 0;
        overlay.style.width = "100%";
        overlay.style.height = "100%";
        overlay.style.cursor = "crosshair";
        wrapper.appendChild(overlay);

        let isDragging = false;
        let startX = 0;
        let startY = 0;
        let rect = null;

        overlay.addEventListener("mousedown", (e) => {
          isDragging = true;
          startX = e.offsetX;
          startY = e.offsetY;

          rect = document.createElement("div");
          rect.style.position = "absolute";
          rect.style.border = "2px solid red";
          rect.style.background = "rgba(255,0,0,0.2)";
          rect.style.left = `${startX}px`;
          rect.style.top = `${startY}px`;
          overlay.appendChild(rect);
        });

        overlay.addEventListener("mousemove", (e) => {
          if (!isDragging || !rect) return;
          const width = e.offsetX - startX;
          const height = e.offsetY - startY;
          rect.style.width = `${Math.abs(width)}px`;
          rect.style.height = `${Math.abs(height)}px`;
          rect.style.left = `${width < 0 ? e.offsetX : startX}px`;
          rect.style.top = `${height < 0 ? e.offsetY : startY}px`;
        });

        overlay.addEventListener("mouseup", (e) => {
          isDragging = false;
          if (rect) {
            const x = parseFloat(rect.style.left);
            const y = parseFloat(rect.style.top);
            const w = parseFloat(rect.style.width);
            const h = parseFloat(rect.style.height);
            console.log("üñºÔ∏è Region drawn:", { x, y, w, h, pageNum });
          }
        });

        container.appendChild(wrapper);
      }
    };

    renderPages();
  }, [pdf]);

  return (
    <div>
      <h3>Storyboard Viewer with Region Comments (Experimental)</h3>
      <div ref={containerRef} style={{ display: "flex", flexDirection: "column", gap: "1rem" }} />
    </div>
  );
};

export default PdfRegionCommenter;
